mapper.select:
  SELECT *
  	FROM MEM
		WHERE M_ID = #{m_id} 
		AND M_PW = #{m_pw}

mapper.insert:

{
	CALL PKG_MEM.PROC_MEM_INSERT
	(
		#{ m_id, mode = IN, jdbcType = VARCHAR },
		#{ m_name, mode = IN, jdbcType = VARCHAR },
		#{ m_pw, mode = IN, jdbcType = VARCHAR },
		#{ tel, mode = IN, jdbcType = VARCHAR },
		#{ email, mode = IN, jdbcType = VARCHAR },
		#{ lvl, mode = IN, jdbcType = VARCHAR }
	)    		
}

mapper.select:
  SELECT M_ID
  	FROM MEM
		WHERE M_NAME = #{m_name} 
		AND TEL = #{tel}
		AND EMAIL = #{email}

  SELECT M_ID, M_NAME ,M_PW, TEL, EMAIL
  	FROM MEM
		WHERE M_ID = #{m_id} 
		AND M_NAME = #{m_name} 
		AND TEL = #{tel}
		AND EMAIL = #{email}

mapper.update:

   	 	UPDATE MEM SET M_PW = #{m_pw} 
			WHERE M_ID =#{m_id} 
			AND M_NAME = #{m_name} 
			AND TEL = #{tel} 
			AND EMAIL = #{email}
